mapper.insert:
    	INSERT INTO sunqubit.sucursales (sucu_id, cont_id, sucu_direccion, sucu_status
    		, sucu_urbanizacion
    		, ubig_id
    		)
			VALUES (#{id}, #{contribuyente.id}, #{direccion}, #{activo}
			, #{urbanizacion}
			, #{ubigeo.id}
			);

mapper.update:
        UPDATE sunqubit.sucursales
		SET sucu_direccion=#{direccion}, sucu_status = #{activo}
			, sucu_urbanizacion = #{urbanizacion}
			, ubig_id = #{ubigeo.id}
		WHERE sucu_id = #{id};

mapper.select:
SELECT * FROM sunqubit.sucursales
	LEFT JOIN sunqubit.ubigeos ON sunqubit.ubigeos.ubig_id = sunqubit.sucursales.ubig_id
WHERE cont_id = #{contId};

SELECT * FROM sunqubit.sucursales
	LEFT JOIN sunqubit.ubigeos ON sunqubit.ubigeos.ubig_id = sunqubit.sucursales.ubig_id
WHERE cont_id = #{contribuyente.id} AND sucu_direccion = #{direccion}
	AND sucu_urbanizacion = #{urbanizacion}
	AND ubig_id = #{ubigeo.id}
	AND sunqubit.ubigeos.ubig_cod_dpto || sunqubit.ubigeos.ubig_cod_prov || sunqubit.ubigeos.ubig_cod_dist = #{ubigeo.codigo}
